import './App.css';

import 'bootstrap/dist/css/bootstrap.min.css';   //minimizing bootstrap use 
import Navbar from './components/NavBar';
import SearchForm from './components/SearchForm';
import RegisterForm from './components/RegisterForm';
import ReadBooks from './components/ReadBooks';

import { BrowserRouter, Route, Link, Switch } from 'react-router-dom';
import { useState, useEffect, createContext } from 'react';
import Home from './components/Home';

export const UserContext = createContext(); 

function App() {
  const [user, setUser] = useState(false);
  const userName = 'Test 1-1';

  // api call for users & logic to check if user is logged in
  const getUser = async () => {
    try {
      const API_ENDPOINT = 'http://localhost:4000/users';
      const response = await fetch (API_ENDPOINT);
      const data = await response.json();
      const currentUser  = data.find((user) => {
        return user.username === userName;
      })
      setUser(currentUser);
      console.log(currentUser);
    } catch (err) {
      console.log(err)
    }
  };

  useEffect(() => {
    getUser();
  }, []);

  return (
    <BrowserRouter>
      <Navbar/>
      <SearchForm/>
      <main>
        <Switch>
          <UserContext.Provider value={{
            user, 
            getUser
          }}>
            <Route
              path='/register'
              component={RegisterForm}
            />
            <Route
              path='/home'
              component={Home}
            />


          </UserContext.Provider>
        </Switch>
      </main>
    </BrowserRouter>
  );
}

export default App;
