import { useState, useContext } from 'react';
import { UserContext } from '../../App';

const CreateReadForm = () => {
    const userContext = useContext(UserContext);

    const initialFormValues = {
        title: '',
        author: '',
        review: '',
    };

    const [values, setValues] = useState(initialFormValues);

    // handle change function
    const _handleChange = (e) => {
        setValues((prevState) => {
            return {
                ...prevState,
                [e.target.id]: e.target.value,
            };
        });
    };

    // create new wish list item function
    const _createNewReadBook = async (e) => {
        e.preventDefault();
        try {
            const API_ENDPOINT = 'http://localhost:4000/read-books';
            const response = await fetch(API_ENDPOINT, {
                method: 'POST',
                body: JSON.stringify({
                    title: values.title,
                    author: values.author,
                    review: values.review,
                    userId: userContext.user._id,
                }),
                headers: {
                    'Content-Type': 'application/json'
                },
            });
            if (response.status === 201)

                setValues(initialFormValues);
        } catch (err) {
            console.log(err)
        }
    };

    return (
        <form onSubmit={_createNewReadBook}>
            <div>
                <label htmlFor='title'>Title</label>
                    <input
                        type='text'
                        id='title'
                        value={values.title}
                        onChange={_handleChange}
                        required
                    />  
            </div>
            <div>
                <label htmlFor='author'>Author</label>
                    <input
                        type='text'
                        id='author'
                        value={values.author}
                        onChange={_handleChange}
                        unique
                    />
            </div>
            <div>
                <label htmlFor='review'>Review</label>
                    <input
                        type='text'
                        id='review'
                        value={values.review}
                        onChange={_handleChange}
                    />
            </div>
            <input type='submit' value='Add Book to Read List' />
        </form>
    )
};

export default CreateReadForm;
