import { useContext, useEffect, useState } from "react";
import { UserContext } from "../../App";
import { useHistory } from "react-router-dom";


const EditForm = ({ props }) => {
    console.log(props);
    const [values, setValues] = useState(initialFormValues);
    const userContext = useContext(UserContext);
    const initialFormValues = {
        title: values.title,
        author: values.author,
        review: values.review,
        userId: values.user._id,
    };

    const history = useHistory();

    const _handleChange = (e) => {
        setValues((prevState) => {
            return {
                ...prevState,
                [e.target.id]: e.target.value,
            };
        });
    };

    const API_ENDPOINT = `http://localhost:4000/read-books/${props.match.params.bookId}`;

    const getBook = async () => {
        try {
            const book = await fetch(API_ENDPOINT);
            const data = await book.json();
            setValues({ title: data.title, author: data.title, review: data.review, userId: data.userId });
        } catch (err) {
            console.log(err)
        }
    };

    useEffect(() => {
        getBook();
    }, []);

    const _updateBook = async (e) => {
        e.preventDefault();
        try {
            const newValues = {};
            if (values.author !== '') {
                newValues.author = values.author;
            };
            if (values.title !== '') {
                newValues.title = values.title
            };
            if (values.title === '' && values.title === ''){
                return;
            };
            const response = await fetch(API_ENDPOINT, {
                method: 'PUT',
                body: JSON.stringify(newValues),
                headers: {
                    'Content-Type': 'application/json',
                },
            })
            if (response.status === 201) {
                userContext.getUser();
                history.push(`/read-books/${props.match.params.bookId}`);
                setValues(initialFormValues);
                console.log('changing book')
            } else {
                alert('Something went wrong. Please try again');
            }
        } catch (err) {
            console.log(err)
        }
    };

    return (
        <div>
            <form onSubmit={_updateBook}> 
                <label htmlFor='title'>Title</label>
                <input 
                    type='text'
                    id='title'
                    value={values.title}
                    onChange={_handleChange}
                />
                <label htmlFor='author'>Author</label>
                <input 
                    type='text'
                    id='author'
                    value={values.author}
                    onChange={_handleChange}
                />
                <input type='submit' value='Update book' />
            </form>
        </div>
    )
};

export default EditForm;
