import { useContext, useEffect } from "react";
import { UserContext } from "../../App";
import { useHistory } from "react-router-dom";
// update user form with fields: first name, last name, password, email(not required)

const EditForm = ({ match }) => {
    const userContext = useContext(UserContext);
    const initialFormValues = {
        title: '',
        author: '',
    };

    const [values, setValues] = useState(initialFormValue);
    const history = useHistory();

    const _handleChange = (e) => {
        setValues((prevState) => {
            return {
                ...prevState,
                [e.target.id]: e.target.value,
            };
        });
    };

    const API_ENDPOINT = `http://localhost:4000/read-books/:${match.params.id}`

    const getBook = async () => {
        try {
            const book = await fetch(API_ENDPOINT);
            const data = await book.json();
            setValues({ title: data.title, author: author.title });
        } catch (err) {
            console.log(err)
        }
    };

    useEffect(() => {
        getBook();
    }, []);

    const _updateBook = async () => {
        e.preventDefault();
        try {
            const response = await fetch(API_ENDPOINT, {
                method: 'PUT',
                body: JSON.stringify(values),
                headers: {
                    'Content-Type': 'application/json',
                },
            })
            if (response.status === 201) {
                userContext.getUser();
                setValues(initialFormValues);
            }
        } catch (err) {
            console.log(err)
        }
    }

    return (
        <div>
            <h1>this is the edit form</h1>
            <form onSubmit={_updateBook}>
                <label htmlFor='title'>Title</label>
                <input 
                    type='text'
                    id='title'
                    value={values.title}
                    onChange={_handleChange}
                />
                <label htmlFor='author'>Author</label>
                <input 
                    type='text'
                    id='author'
                    value={values.author}
                    onChange={_handleChange}
                />
                <input type='submit' value='update book' />
            </form>
        </div>
    )
};

export default EditForm;
